mapper.select:
SELECT id, nickName, password, email, inserted
FROM Member
WHERE nickName = #{nickName}

SELECT id, nickName, password, email, inserted
FROM Member
WHERE email = #{email}

mapper.update:
UPDATE Member
SET
	nickName = #{nickName},
	password = #{password}, 
	email = #{email}
WHERE id = #{id}

mapper.select:
SELECT id, nickName, password, email, inserted
FROM Member
WHERE id = #{id}
